defineService({name:"Crasher",onStart:async t=>{t.state.tickCount=0,Neo.log.info("Crasher service started - will crash on tick #3"),t.state.intervalId=setInterval(()=>{if(t.state.tickCount+=1,Neo.log.info("Crasher tick #"+t.state.tickCount),t.state.tickCount===3)throw Neo.log.warn("Crasher: About to throw an exception!"),new Error("Intentional crash to test isolation!")},3e3)},onStop:async t=>{t.state.intervalId&&clearInterval(t.state.intervalId),Neo.log.info("Crasher service stopped")}});
