defineService({name:"System Watchdog",onStart:async e=>{e.state.startTime=Date.now(),e.state.checks=0,Neo.log.info("Watchdog service started"),e.state.intervalId=setInterval(()=>{e.state.checks+=1;const t=Math.floor((Date.now()-e.state.startTime)/1e3);Neo.log.debug("Watchdog check #"+e.state.checks+" - uptime: "+t+"s")},1e4)},onStop:async e=>{e.state.intervalId&&clearInterval(e.state.intervalId);const t=Math.floor((Date.now()-e.state.startTime)/1e3);Neo.log.info("Watchdog stopped - uptime: "+t+"s, checks: "+e.state.checks)}});
